tags.onInstJoined:
@if (that.inst !== os.getCurrentInst()) {
    return;
}

const ab1Core = getBot(byTag('abID', 'learn'));
if (ab1Core) {
    console.log('[ab1 bootstrap] ab1 already detected. Skipping bootstrap.');
    return;
}
try {
    let aux = os.device().ab1BootstrapAux;

    if(!aux) {
        const response = await webhook({
            method: "GET",
            url: os.getAB1BootstrapURL()
        });

        if (response.status !== 200) {
            console.log('[ab1 bootstrap] Could not download installer.');
            return;
        }

        aux = response.data;
    }

    if (aux.version === 2) {
        const updates = aux.updates;

        const state = await os.getInstStateFromUpdates(updates);
        const ids = Object.keys(state);
        await os.applyUpdatesToInst(updates);

        whisper(ids, tagName, that);
    } else {
        const dataOutput = aux.state;

        // console.log(dataOutput)

        const newBots = [];

        for (const property in dataOutput) {
            const newBot = dataOutput[property];

            if (newBot.space) {
                let b = create(newBot.tags);
                b.tags.creator = null;
                newBots.push(b);
            }
            else {
                console.log("skipped bot: " + newBot);
            }
        }

        whisper(newBots, tagName, that);
    }
    console.log(`[ab1 bootstrap] Installed ab1 version ${aux.version}!`);
} catch (e) {
    console.log('[ab1 bootstrap] Could not download installer.');
    return;
}
