tags.commandNewBuilder:
@let [name] = that || [];
if (name) {
    const existing = getBots(...getFilters(name));
    if (existing.length > 0) {
        player.toast(`Builder ${name} already exists`);
        return;
    }
    const filters = getFilters(tags.builderName);
    const mod = {
        builderName: name
    };
    const builder = create(this, mod);
    const others = getBots(...filters, byTag("#auxBuilderLibrary"));
    create(others, {
        auxCreator: builder.id,
        builderName: name
    });
}

function getFilters(name) {
    return [byTag("#builderName", name), bySpace("shared")];
}

tags.onTapCode:
@const isShowChat = that === "3342";
const isHideChat = that.endsWith("5");
if (!isShowChat && !isHideChat) {
    return;
}
const [allowed] = whisper(this, "builderShouldHandleBootstrap");
if (!allowed) {
    return;
}
if (isShowChat) {
    whisper(this, "commandShowChat");
} else if(isHideChat) {
    player.hideChat();
}

tags.onKeyUp:
@if (that.keys.indexOf("`") < 0) {
    return;
}

const [allowed] = whisper(this, "builderShouldHandleBootstrap");
if (!allowed) {
    return;
}
whisper(this, "commandShowChat");

tags.commandDownloadBuilder:
@const bots = getBots(
    bySpace("shared"),
    byTag("#builderName", tags.builderName),
);

const badTags = [
    'builderState',
    'builderColor',
    'auxBuilderX',
    'auxBuilderY',
    'auxBuilderSortOrder',
];
const filtered = bots.map(b => {
    const mod = { ...b.raw };
    for (let tag of badTags) {
        delete mod[tag];
    }
    return {
        id: b.id,
        tags: mod
    };
});

player.downloadBots(filtered, `${tags.builderName}.aux`);

tags.builderSleepOthers:
