    shout("builderOnClick");
}

tags.builderShouldHandleBootstrap:
@const isActive = tags.builderState === "Enabled";
if (isActive) {
    return true;
}
if (tags.builderName === "ab-1") {
    const activeBuilders = getBots(inDimension("auxBuilder"), byTag("builderState", "Enabled"));
    return activeBuilders.length <= 0;
}
return false;

tags.onChat:
@const wakeRegex = /^\.\.\s?([\s\w-_]+)?$/gi;
const wakeResult = wakeRegex.exec(that.message);
if (wakeResult) {
    const [_, builderName] = wakeResult;
    if (builderName === tags.builderName || (!builderName && tags.builderName === "ab-1")) {
        whisper(this, "builderSleepOthers");
        whisper(this, "builderWake");
    }
}
const commands = tags.chatCommands;
for(let command of commands) {
    if (command.test) {
        const result = command.test.exec(that.message);
        if (!result) {
            continue;
        }
        if (tags.builderState !== "Enabled") {
            const otherActiveBuilders = getBots(
                inDimension("#auxBuilder"),
                byTag("#builderName", n => n !== tags.builderName),
                byTag("#builderState", "Enabled"));

            if (otherActiveBuilders.length <= 0) {
                player.toast("Builder is sleeping");
            }
        } else {
            shout("builderCommand", { command: command.shout, argument: result.slice(1) });
        }
        break;
    }
}

