tags.calculateMenu:
@const map = tags.menuMap;
for(let menu of map) {
    const filter = menu.filter;
    let matches = true;
    for(let tag of Object.keys(filter)) {
        let value = tags[tag];
        let expected = filter[tag];
        if (value !== expected) {
            matches = false;
            break;
        }
    }
    if (matches) {
        return menu;
    }
}
return null;

