tags.createMenu:
@const commands = that;
const menuParent = create({ space: 'tempLocal' });
const pb = player.getBot();
const mods = commands.filter(c => { 
    return !!c.label && (!c.filter || c.filter());
}).map(c => ({
    space: 'tempLocal',
    auxCreator: menuParent.id,
    auxLabel: c.label,
    auxBuilderMenu: true,
    menuShout: c.shout,
    menuUpdate: c.update,
    onClick: `@shout("builderMenuItemSelected", { command: tags.menuShout, updateMenu: tags.menuUpdate })`,
}));

create(mods);
pb.tags.builderMenuId = menuParent.id;
pb.tags.auxMenuPortal = "auxBuilderMenu";
pb.tags.auxMenuPortalConfigBot = menuParent.id;

tags.destroyMenu:
@const pb = player.getBot();
if (pb.tags.builderMenuId) {
    destroy(pb.tags.builderMenuId);
    tags.currentMenuId = null;
}

tags.updateMenu:
@const current = tags.currentMenuId;
const [finalMenu] = whisper(this, "calculateMenu");
let changed = false;
if (!current && finalMenu) {
    changed = true;
} else if (current && !finalMenu) {
    changed = true;
} else if (!current && !finalMenu) {
    changed = false;
} else if (current !== finalMenu.menuId) {
    changed = true;
}

if (changed) {
    whisper(this, "destroyMenu");
    tags.currentMenuId = finalMenu ? finalMenu.menuId : null;
    const [menu] = whisper(this, "getCurrentMenu");
    if (menu) {
        whisper(this, "createMenu", menu.commands)
    }
}

