tags.onPlayerPortalChanged:
@let changed = false;
if (that.portal === 'auxPagePortal' && that.dimension) {
    console.log("Changed " + that.portal + " to " + that.dimension);
    if (that.dimension === 'auxBuilder') {
        changeState(this, "Builder", "dimensionState");
        changed = true;
    } else {
        // Main portal changed
        const bots = getBots(inDimension(that.dimension), not(byTag("#builderName"), tags.builderName));

        if (bots.length === 0) {
            changeState(this, "Transition", "dimensionState");
            changeState(this, "Empty", "dimensionState");
            changed = true;
        } else {
            changeState(this, "Transition", "dimensionState");
            changeState(this, "Filled", "dimensionState");
            changed = true;
        }
    }
}
if (changed) {
    whisper(this, "updateMenu");
}

